package casting;

public class Conversion {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int i=200;
	long l=i;
	float f=l;
	System.out.println("Int value"+i);
	System.out.println("Long value"+l);
	System.out.println("Float value"+f);
	}

}
